const divClassRight = "flex flex-col w-full h-full ml-40";
const divClassLeft = "flex flex-col w-full h-full ml-56";

function Boards ({code, narabic, nbangla, nenglish, address, email, phone}){
    return(
        <div className="mt-1 flex flex-col w-full h-full ">
            <div className={divClassLeft}>
                <p className="font-serif">{code}</p>
                <p className="text-xl text-red-500 font-bold font-serif">{narabic}</p>
                <p className="text-xl text-green-700 font-bold font-serif">{nbangla}</p>
                <p className="text-xl text-blue-700 font-bold font-serif">{nenglish}</p>
                <p className="text-lg font-serif">{address}</p>
                <p className="text-lg font-serif">{email}</p>
                <p className="text-lg font-serif">{phone}</p>
            </div>              
        </div>
    )
}
function BoardList(){
    const [madrasaL, setMadrasaL] = useState("");

        fetch('http://localhost:3001/boards')
            .then((response) => response.json())
            .then((data) => {
               const d = data.map(k =>
                        <Boards
                            code={k.code_no}
                            mnamearabic={k.name_arabic}
                            mnamebangali={k.name_bangala}
                            mnameenglish={k.name_english}
                            address={k.address}
                            phone={k.phone}
                            email={k.e_mail}
                            muhtamim={k.muhtamim}
                            nazim={k.nazim}
                            jela={k.jela}
                            divis={k.division} />)
                        setMadrasaL(d)
            });
    return(
        <div className="w-full h-full flex mt-20 mb-56">
            <div className="w-[10%] h-full"></div>

                <div className="w-[80%] h-full bg-gray-100 ">        
                    <p className="mt-5 mb-10 text-3xl text-red-700 font-bold font-serif text-left text-center">Boards Under Al-Haiatul Ulya</p>
                    <div className="flex w-full h-full justify-center divide-x-2 divide-slate-500">
                        <div className="mt-1 flex flex-col w-full h-full ">
                            <div className={divClassLeft}>

                            </div>
                            <div className={divClassLeft}>
                            </div>
                            <div className={divClassLeft}>
                            </div>
                        </div>
                        <div className={"mt-1 flex flex-col w-full h-full ml-40"}>
                            <div className={divClassRight}>
                            </div>
                            <div className={divClassRight}>
                            </div>
                            <div className={divClassRight}>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="w-[10%]" ></div>

        </div>
    )
}
export default BoardList;
