import { useState } from "react";
import data from "./data";

const inputclass = "h-[10%] w-[65%] p-4 border border-blue-700 rounded-md m-3";
function AddStaf({labelName, type, value, set}){
    return(
        <div className="mt-1 flex">
            <label className="ml-4 pr-2 mt-7 ">{labelName}
            </label>    
                        <input 
                            type={type}
                            value= {value} 
                            onChange={set}  
                            className={inputclass}
                        />
        </div>
    )
}
const inputArray = [
    { label: "01. Name :", type: "text", value: "{name}", set: "{e => setName(e.target.value)}" },
    { label: "02. Father Name :", type: "text", value: "{fother}", set: "{e => setFather(e.target.value)}" },
    { label: "03. Mother Name :", type: "text", value: "{mother}", set: "{e => setMother(e.target.value)}" },
    { label: "04. Present Address :", type: "text", value: "{paddress}", set: "{e => setPAddress(e.target.value)}" },
    { label: "05. Permanent Address :", type: "text", value: "{peraddress}", set: "{e => setPerAddress(e.target.value)}" },
    { label: "06. Academic Achievement :", type: "text", value: "{acadamic}", set: "{e => setAcademic(e.target.value)}" },
    { label: "07. Languages Skills :", type: "text", value: "{language}", set: "{e => setLanguage(e.target.value)}" },
    { label: "08. Designation :", type: "text", value: "{designation}", set: "{e => setDesignation(e.target.value)}" },
    { label: "09. Contact_no :", type: "text", value: "{phone}", set: "{e => setPhone(e.target.value)}" },
    { label: "10. E-Mail :", type: "text", value: "{email}", set: "{e => setEmail(e.target.value)}" },
    { label: "11. Room No :", type: "text", value: "{room}", set: "{e => setRoom(e.target.value)}" },
    { label: "12. image :", type: "text", value: "{image}", set: "{e => setImage(e.target.value)}" },
    { label: "13. Natinality :", type: "text", value: "{nationality}", set: "{e => setNationality(e.target.value)}" },
    { label: "14. Date of Brith :", type: "text", value: "{brith}", set: "{e => setBrith(e.target.value)}" },
    { label: "15. NID :", type: "text", value: "{nid}", set: "{e => setNid(e.target.value)}" },
]

const w = inputArray.map(r =>
             <AddStaf 
                labelName={r.label}
                type={r.type}
                value={r.value}
                set={r.set}
                />)
function StaffForm(){
    const [name, setName] = useState("");
    const [father, setFather] = useState("");
    const [mother, setMother] = useState("");
    const [paddress, setPAddress] = useState("");
    const [peraddress, setPerAddress] = useState("");
    const [academic, setAcademic] = useState("");
    const [language, setLanguage] = useState("");
    const [designation, setDesignation] = useState("");
    const [Phone, setPhone] = useState("");
    const [email, setEmail] = useState("");
    const [room, setRoom] = useState("");
    const [image, setImage] = useState("");
    const [nationality, setNationality] = useState("");
    const [brith, setBrith] = useState("");
    const [nid, setNid] = useState("");

    const submit = ()=>{
        const user = {
            name: name,
            father: father,
            mother: mother,
            present_address: paddress,
            permanent_ddress: peraddress,
            academic_achievement: academic,
            languages_skills: language,
            designation: designation,
            contact_no: Phone,
            e_mail: email,
            room_no: room,
            image: image,
            nationality: nationality, 
            brith: brith, 
            nid: nid,

        };
        fetch('http://localhost:3001/create-employee', {
            method: 'POST', // or 'PUT'
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(user),
        })
            .then((response) => window.alert("Data is Sent"));
    };
    return(
        <div className="w-full h-screen mt-40 ">

                <div className="w-full h-4/6 bg-gray-100 p-5 ">        
                    <p className="mt-1 ml-5 text-left underline mr-10">Add New Staff Information</p>
                    {w}
                    <div className="ml-12 pr-3 pl-8">
                        <button 
                            type="button"
                            onClick={submit} 
                            className="text-center text-1xl text-white font-bold h-[80%] w-[80%] ml-10 bg-blue-600 hover:text-blue-300 p-3 border rounded-md ">Submit</button>
                    </div>
                </div>



        </div>
    );
}
export default StaffForm;
